mapper.insert:

        SELECT IFNULL(MAX(USER_NO), 0) + 1
          FROM TB_USER       

	INSERT
	  INTO TB_USER
	     ( USER_NO
	     , USER_ID
	     , USER_PW
	     , AUTH_TYPE
	     , KAKAO_ID
	     , NAVER_ID
	     , GOOGLE_ID
	     , FACEBOOK_ID
	     , USER_BIRTH
	     , USER_NAME
	     , GENDER
	     , MOBILE_NO
	     , EMAIL
	     , ZIP
	     , ADDRESS
	     , THUMB_IMG
	     , USER_ROLE
	     , USE_YN
	     , LAST_LOGIN_DT
	     , LOGIN_CO
	     , ACCEPT
	     , REGIST_DT
	     , UPDT_DT
	     )
	VALUES
	     ( #{userNo}
	     , #{userId}
	     , #{userPw}
	     , #{authType}
	     , #{kakaoId}
	     , #{naverId}
	     , #{googleId}
	     , #{facebookId}
	     , #{userBirth}
	     , #{userName}
	     , #{gender}
	     , #{mobileNo}
	     , #{email}
	     , #{zip}
	     , #{address}
	     , #{thumbImg}
	     , #{userRole}
	     , #{useYn}
	     , DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
	     , #{loginCo}
	     , #{accept}
	     , DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
	     , #{updtDt}
	     )
